import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Profile.css'; // Import the CSS file
import dummyPhoto from '../assets/images/user.png'; // Import the dummy photo
import { updateUser } from '../apis/Api'; // Import the updateUser function

const Profile = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [formData, setFormData] = useState({
    firstName: user.firstName,
    lastName: user.lastName,
    email: user.email,
    phoneNo: user.phoneNo,
    avatar: user.avatar,
  });

  const handleSubmit = async () => {
    try {
      const response = await updateUser(user._id, formData);
      console.log('Profile updated successfully:', response.data);

    } catch (error) {
      console.error('Error updating profile:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePhotoChange = async (e) => {
    try {
      const newAvatar = // Logic to get the new avatar URL after uploading the image;
      setFormData({
        ...formData,
        avatar: newAvatar,
      });
      const response = await updateUser(user._id, formData);
      console.log('User profile updated:', response.data);
      // Optionally, update the user data in localStorage here
    } catch (error) {
      console.error('Error updating user profile:', error.message);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <Link to="/dashboards" className="back-button">&#8249; Back</Link>
        <h2 className="profile-title">Profile</h2>
      </div>
      <div className="profile-info">
        <div className="profile-photo">
          <img src={dummyPhoto} alt="Dummy Photo" className="profile-photo-image" />
          <input type="file" onChange={handlePhotoChange} />
        </div>
        <div className="profile-fields">
          <div className="profile-field">
            <label className="profile-label">First Name:</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Last Name:</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Phone Number:</label>
            <input
              type="tel"
              name="phoneNo"
              value={formData.phoneNo}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Status:</label>
            <div className="profile-value">{user.is_verified ? 'Verified' : 'Not Verified'}</div>
          </div>
        </div>
      </div>

      <button className="profile-button" onClick={handleSubmit}>
        Save Changes
      </button>
    </div>
  );
};

export default Profile;
