// UserProfile.js

import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function UserProfile() {
  const { id } = useParams();
  const [user, setUser] = useState({});
  const [license, setLicense] = useState({});

  useEffect(() => {
    // Fetch user profile and license details from the backend API
    // Replace the API endpoints with your actual endpoints
    fetch(`http://localhost:5000/api/admin/viewUserProfile/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setUser(data.user);
        setLicense(data.license);
      })
      .catch((error) => console.error("Error fetching user profile:", error));
  }, [id]);

  return (
    <div>
      <h1>User Profile</h1>
      <h2>User Details</h2>
      <p>
        Name: {user.firstName} {user.lastName}
        <br />
        Email: {user.email}
        <br />
        Phone Number: {user.phoneNo}
      </p>
      <h2>License Details</h2>
      {license ? (
        <div>
          License Name: {license.licenseName}
          <br />
          <img src={license.licenseImageUrl} alt="License" style={{ maxWidth: "300px" }} />
        </div>
      ) : (
        <p>No license details available</p>
      )}
    </div>
  );
}

export default UserProfile;
