import React, { useState } from 'react';
import './Sidebar.css'; // Import your CSS file if you have one
import { Dashboard } from './dashboard';

const Sidebar = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const handleToggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
      <div className="logo-details">
      <i class="bi bi-list" style={{color:""}}></i>
        <div className="logo_name">Drivers App</div>
        <i className={`bx ${isSidebarOpen ? 'bx-menu-alt-right' : 'bx-menu'}`} id="btn" onClick={handleToggleSidebar}></i>
      </div>
      <ul className="nav-list text-white">
        <li>
          <i className='bx bx-search' ></i>
         Home
        </li>
       <li>

       </li>
      </ul>
      {/* Your profile section */}
    </div>
  );
};

const DashboardSection = () => (
  <section className="home-section">
   <Dashboard/>
  </section>
);

const MainDashboard = () => (
  <div className="app-container">
    <Sidebar />
    <DashboardSection />
  </div>
);

export default MainDashboard;
