import React, { useState, useEffect } from 'react';
import { getAllBluebooksApi, updateBluebookStatusApi } from '../apis/Api'; // Update this import if you have an API for updating bluebook status
import { Link } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AdminDashboard = () => {
  const [bluebooks, setBluebooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBluebooks();
  }, []);

  const fetchBluebooks = async () => {
    try {
      const response = await getAllBluebooksApi();
      setBluebooks(response.data.bluebooks);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching bluebooks:', error);
      toast.error('Error fetching bluebooks');
    }
  };

  const handleApproveBluebook = async (bluebookId) => {
    try {
      // Update the bluebook status to "approved" in the backend
      await updateBluebookStatusApi(bluebookId, { bluebookStatus: 'approved' }); // Update this function according to your API
      // Show a success message
      toast.success('Bluebook approved successfully');
      // Fetch the bluebooks again to reflect the changes
      fetchBluebooks();
    } catch (error) {
      console.error('Error approving bluebook:', error);
      toast.error('Error approving bluebook');
    }
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      {loading ? (
        <p>Loading...</p>
      ) : bluebooks.length === 0 ? (
        <div>
          <p>No bluebook entries found.</p>
        </div>
      ) : (
        <div>
          {bluebooks.map(bluebook => (
            <div key={bluebook._id}>
              <h3>{bluebook.bluebookName}</h3>
              <p>Vehicle Number: {bluebook.vehicleNumber}</p>
              <p>Status: {bluebook.bluebookStatus}</p>
              <p>Chassis Number: {bluebook.chassisNumber}</p>
              <p>Engine Number: {bluebook.engineNumber}</p>
              {/* Show the approve button only if the status is not approved */}
              {bluebook.bluebookStatus !== 'approved' && (
                <button onClick={() => handleApproveBluebook(bluebook._id)}>Approve</button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
